beginning.embedded_functions:
bool em_check_trolley_pos( double val ){

	if( val < -32.23 ){
		//debug: print out to console
		printf("5eceived hoist_pos: %.5f\n", val);
		return true;
	} else
		return false;
}

//any numeric values in mmt-security are "double" 
// (even integer ou float, they are stored in a double)
bool em_check_marker(double x, double y){
	//debug
	printf("received marker x=%.0f, y=%.0f\n", x, y);
	//just an example:
	return (x >= 100 && y >= 100);
}
