//any numeric values in mmt-security are "double" 
// (even integer ou float, they are stored in a double)

static bool em_coor(double pos, double spd_ref){
       bool result = true;
       if(((pos < 2) && (pos > 0)) || ((pos > 4) && (pos < 6))){
              if (spd_ref!=0.0f){
                     result = false;
              }
       }
       return result;
}

